/* global gapi */ // Inform ESLint that gapi is globally defined
import React, { useEffect, useState } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import { getAuth, GoogleAuthProvider, signInWithPopup } from "firebase/auth";
import firebaseApp from "../firebase";

const CLIENT_ID = "100174910445-i34qd8f9l36pipfd4cce62jhm1u3be8h.apps.googleusercontent.com";
const SCOPES = "https://www.googleapis.com/auth/calendar.events https://www.googleapis.com/auth/calendar";

function Reserve() {
  const [events, setEvents] = useState([]); // Calendar events
  const [token, setToken] = useState(null); // Google OAuth token
  const [user, setUser] = useState(null); // Firebase authenticated user
  const [error, setError] = useState(null); // Error messages
  const [selectedRoom, setSelectedRoom] = useState(""); // Selected room
  const [availableTimes, setAvailableTimes] = useState([]); // Available times
  const [formData, setFormData] = useState({
    date: "",
    time: "",
    email: "",
  });

  const rooms = [
    { id: "room1", name: "Room 1", times: ["09:00", "10:00", "11:00"] },
    { id: "room2", name: "Room 2", times: ["12:00", "13:00", "14:00"] },
  ];

  const auth = getAuth(firebaseApp);

  // Initialize gapi client on component mount
  useEffect(() => {
    const initializeGapiClient = async () => {
      if (typeof gapi !== "undefined") {
        await gapi.load("client:auth2", async () => {
          await gapi.client.init({
            clientId: CLIENT_ID,
            scope: SCOPES,
          });
          console.log("Google API client initialized");
        });
      } else {
        console.error("gapi is not defined. Make sure the script is loaded.");
      }
    };

    initializeGapiClient();
  }, []);

  // Firebase authentication and get Google access token
  const handleFirebaseLogin = async () => {
    try {
      const provider = new GoogleAuthProvider();
      const result = await signInWithPopup(auth, provider);
      const credential = GoogleAuthProvider.credentialFromResult(result);
      const accessToken = credential.accessToken;
  
      console.log("Access Token received:", accessToken); // Debug log
      setUser(result.user);
      setToken(accessToken);
  
      if (accessToken) {
        loadEvents(accessToken);
      } else {
        console.error("Access Token is null or undefined.");
        setError("Failed to obtain access token.");
      }
    } catch (error) {
      console.error("Firebase login error:", error);
      setError("Failed to log in. Please try again.");
    }
  };  

  // Load events from Google Calendar
  const loadEvents = (accessToken) => {
    if (!accessToken) {
      console.error("No Access Token available for loading events.");
      setError("Failed to load events due to missing access token.");
      return;
    }
  
    console.log("Loading events with Access Token:", accessToken);
  
    gapi.client
      .request({
        path: "https://www.googleapis.com/calendar/v3/calendars/primary/events",
        method: "GET",
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      })
      .then((response) => {
        console.log("Google Calendar API Response:", response); // Log response
        const formattedEvents = response.result.items.map((event) => ({
          title: event.summary,
          start: event.start.dateTime,
          end: event.end.dateTime,
        }));
        setEvents(formattedEvents);
        console.log("Formatted Events:", formattedEvents); // Debug log
      })
      .catch((error) => {
        console.error("Error loading Google Calendar events:", error);
        setError("Failed to load events. Please try again.");
      });
  };
  

  // Add a reservation to Google Calendar
  const addReservationToGoogleCalendar = (reservation) => {
    if (!token) {
      setError("No token available for adding reservations.");
      return;
    }

    console.log("Adding reservation:", reservation); // Debug log

    gapi.client
      .request({
        path: "https://www.googleapis.com/calendar/v3/calendars/primary/events",
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          summary: `Room ${reservation.room_id} - Reserved by ${reservation.email}`,
          start: {
            dateTime: reservation.start_time,
            timeZone: "UTC",
          },
          end: {
            dateTime: reservation.end_time,
            timeZone: "UTC",
          },
        }),
      })
      .then(() => {
        setEvents((prevEvents) => [
          ...prevEvents,
          {
            title: `Room ${reservation.room_id} - Reserved by ${reservation.email}`,
            start: reservation.start_time,
            end: reservation.end_time,
          },
        ]);
        alert("Reservation confirmed and added to Google Calendar.");
      })
      .catch((error) => {
        console.error("Error adding reservation to Google Calendar:", error);
        setError("Failed to add reservation. Please try again.");
      });
  };

  const handleRoomChange = (e) => {
    const room = rooms.find((r) => r.id === e.target.value);
    setSelectedRoom(room.id);
    setAvailableTimes(room.times);
  };

  const handleReservation = (e) => {
    e.preventDefault();

    if (!token) {
      setError("You must be logged in to add a reservation.");
      return;
    }

    const newReservation = {
      room_id: selectedRoom,
      email: formData.email,
      start_time: `${formData.date}T${formData.time}:00`,
      end_time: `${formData.date}T${parseInt(formData.time.split(":")[0]) + 1}:00:00`,
    };

    addReservationToGoogleCalendar(newReservation);

    setFormData({ date: "", time: "", email: "" });
  };

  return (
    <div>
      <h1>Room Reservations</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}

      {!user ? (
        <button onClick={handleFirebaseLogin}>Login with Google</button>
      ) : (
        <p>Welcome, {user.displayName}!</p>
      )}

      <form onSubmit={handleReservation}>
        <label>Room: </label>
        <select onChange={handleRoomChange} value={selectedRoom} required>
          <option value="" disabled>
            Select a room
          </option>
          {rooms.map((room) => (
            <option key={room.id} value={room.id}>
              {room.name}
            </option>
          ))}
        </select>
        <label>Date: </label>
        <input
          type="date"
          value={formData.date}
          onChange={(e) => setFormData({ ...formData, date: e.target.value })}
          required
        />
        <label>Time: </label>
        <select
          value={formData.time}
          onChange={(e) => setFormData({ ...formData, time: e.target.value })}
          required
        >
          <option value="" disabled>
            Select a time
          </option>
          {availableTimes.map((time) => (
            <option key={time} value={time}>
              {time}
            </option>
          ))}
        </select>
        <label>Email: </label>
        <input
          type="email"
          value={formData.email}
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          required
        />
        <button type="submit">Reserve</button>
      </form>

      <FullCalendar
        plugins={[dayGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        events={events}
        headerToolbar={{
          left: "prev,next today",
          center: "title",
          right: "dayGridMonth,dayGridWeek,dayGridDay",
        }}
      />
    </div>
  );
}

export default Reserve;
